    filteredData = filterByProtein(filteredData, userData.proteinPreference);
    filteredData = filterByCarbohydrate(filteredData, userData.carbohydratePreference);
    filteredData = filterByFat(filteredData, userData.fatPreference);
end
function filteredData = filterByCalories(nutrientData, calories)
    % Filter the nutrient data by calories
    filteredData = nutrientData(nutrientData.calories <= calories);
end
function filteredData = filterByProtein(nutrientData, proteinPreference)
    % Filter the nutrient data by protein preference
    filteredData = nutrientData(nutrientData.protein >= proteinPreference.min && nutrientData.protein <= proteinPreference.max);
end
function filteredData = filterByCarbohydrate(nutrientData, carbohydratePreference)
    % Filter the nutrient data by carbohydrate preference
    filteredData = nutrientData(nutrientData.carbohydrate >= carbohydratePreference.min && nutrientData.carbohydrate <= carbohydratePreference.max);
end
function filteredData = filterByFat(nutrientData, fatPreference)
    % Filter the nutrient data by fat preference
    filteredData = nutrientData(nutrientData.fat >= fatPreference.min && nutrientData.fat <= fatPreference.max);
end
function [protein, carbohydrate, fat] = calculateMacronutrientBreakdown(userData)
    % Calculate the macronutrient breakdown for the meal
    totalCalories = userData.calories;
    proteinRatio = userData.proteinRatio;
    carbohydrateRatio = userData.carbohydrateRatio;
    fatRatio = userData.fatRatio;

    protein = totalCalories * proteinRatio / 4;
    carbohydrate = totalCalories * carbohydrateRatio / 4;
    fat = totalCalories * fatRatio / 9;
end
function mealIngredients = selectIngredients(filteredData, protein, carbohydrate, fat)
    % Select ingredients randomly from the filtered data
    totalWeight = protein + carbohydrate + fat;
    proteinWeight = protein / totalWeight;
    carbohydrateWeight = carbohydrate / totalWeight;
    fatWeight = fat / totalWeight;

    proteinIngredients = filteredData(filteredData.protein > 0);
    carbohydrateIngredients = filteredData(filteredData.carbohydrate > 0);
    fatIngredients = filteredData(filteredData.fat > 0);

    proteinSelection = selectRandomIngredients(proteinIngredients, proteinWeight);
    carbohydrateSelection = selectRandomIngredients(carbohydrateIngredients, carbohydrateWeight);
    fatSelection = selectRandomIngredients(fatIngredients, fatWeight);

    mealIngredients = [proteinSelection, carbohydrateSelection, fatSelection];
end
function selectedIngredients = selectRandomIngredients(ingredientData, weight)
    % Select random ingredients from the ingredient data based on the weight
    selectedIngredients = [];
    while weight > 0 && ~isempty(ingredientData)
        index = randi(length(ingredientData));
        ingredient = ingredientData(index);
        selectedIngredients = [selectedIngredients, ingredient];
        weight = weight - ingredient.weight;
        ingredientData(index) = [];
    end
end
