bool isValid(string s) {
    int n = s.length();
    if (n&1) return false; // odd length

    char stack[n];
    int top=-1;

    for(int i=0;i<n; i++)
    {
        char ch = s[i];
        if (ch=='(' || ch=='[' || ch=='{')
        {
            stack[++top] = ch;
        }
        else if (ch == ')' || ch == ']' || ch == '}')
