            if (temp == '(' && ch != ')') return false;
            else if (temp == '[' && ch != ']') return false;
            else if (temp == '{' && ch != '}') return false;
        }
    }

    // check stack empty or not
    if (top == -1)
        return true;
    else
        return false;
}

