import org.apache.commons.csv.CSVParser;
import org.apache.commons.csv.CSVRecord;
import org.apache.commons.lang3.StringUtils;
import org.apache.commons.math3.linear.Array2DRowRealMatrix;
import org.apache.commons.math3.linear.RealMatrix;
import org.apache.commons.math3.stat.correlation.PearsonsCorrelation;
public class SentimentAnalysis {
    public static void main(String[] args) throws IOException {
        // Load dataset
        List<String> reviews = new ArrayList<>();
        List<String> sentiments = new ArrayList<>();
        BufferedReader reader = new BufferedReader(new FileReader("customer_reviews.csv"));
        CSVParser csvParser = new CSVParser(reader, CSVFormat.DEFAULT);
        for (CSVRecord csvRecord : csvParser) {
            reviews.add(csvRecord.get(0));
            sentiments.add(csvRecord.get(1));
        }
        reader.close();

        // Split dataset into training and testing sets
        List<Integer> indices = IntStream.range(0, reviews.size()).boxed().collect(Collectors.toList());
        Random random = new Random();
        int testSize = (int) (0.2 * reviews.size());
        List<Integer> testIndices = new ArrayList<>();
        while (testIndices.size() < testSize) {
            int index = indices.remove(random.nextInt(indices.size()));
            testIndices.add(index);
        }
        List<String> trainReviews = new ArrayList<>();
        List<String> trainSentiments = new ArrayList<>();
        List<String> testReviews = new ArrayList<>();
        List<String> testSentiments = new ArrayList<>();
        for (int i = 0; i < reviews.size(); i++) {
            if (testIndices.contains(i)) {
                testReviews.add(reviews.get(i));
                testSentiments.add(sentiments.get(i));
            } else {
                trainReviews.add(reviews.get(i));
                trainSentiments.add(sentiments.get(i));
            }
        }

        // Create bag of words
        List<String> vocabulary = new ArrayList<>();
        for (String review : trainReviews) {
            String[] words = review.split("\\s+");
            for (String word : words) {
                if (!vocabulary.contains(word)) {
                    vocabulary.add(word);
                }
            }
        }
        RealMatrix trainMatrix = new Array2DRowRealMatrix(trainReviews.size(), vocabulary.size());
        for (int i = 0; i < trainReviews.size(); i++) {
            String[] words = trainReviews.get(i).split("\\s+");
            for (int j = 0; j < vocabulary.size(); j++) {
                int count = StringUtils.countMatches(trainReviews.get(i), vocabulary.get(j));
                trainMatrix.setEntry(i, j, count);
            }
        }
        RealMatrix testMatrix = new Array2DRowRealMatrix(testReviews.size(), vocabulary.size());
        for (int i = 0; i < testReviews.size(); i++) {
            String[] words = testReviews.get(i).split("\\s+");
            for (int j = 0; j < vocabulary.size(); j++) {
                int count = StringUtils.countMatches(testReviews.get(i), vocabulary.get(j));
                testMatrix.setEntry(i, j, count);
            }
        }

        // Train model using Naive Bayes classifier
        RealMatrix trainSentimentMatrix = new Array2DRowRealMatrix(trainSentiments.size(), 1);
        for (int i = 0; i < trainSentiments.size(); i++) {
            trainSentimentMatrix.setEntry(i, 0, Double.parseDouble(trainSentiments.get(i)));
        }
        PearsonsCorrelation correlation = new PearsonsCorrelation(trainMatrix.transpose(), trainSentimentMatrix);
        RealMatrix weights = correlation.getCorrelationMatrix().getColumnMatrix(0);
        RealMatrix trainPredictions = trainMatrix.multiply(weights);
        RealMatrix testPredictions = testMatrix.multiply(weights);
        List<String> trainPredSentiments = new ArrayList<>();
