private static string CreateOAuthHeader(string consumerKey, string accessToken, string oauthSignature)
{
    return string.Format("OAuth oauth_consumer_key=\"{0}\", oauth_nonce=\"{1}\", oauth_signature=\"{2}\", oauth_signature_method=\"HMAC-SHA1\", oauth_timestamp=\"{3}\", oauth_token=\"{4}\", oauth_version=\"1.0\"",
        consumerKey,
        Guid.NewGuid().ToString("N"),
        HttpUtility.UrlEncode(oauthSignature, Encoding.UTF8),
        ((int)(DateTime.UtcNow - new DateTime(1970, 1, 1)).TotalSeconds).ToString(),
        accessToken);
}
private static string CreateOAuthSignature(string httpMethod, string url, string consumerKey, string consumerSecret, string accessToken, string accessTokenSecret, Dictionary<string, string> parameters)
{
    // Add the OAuth parameters
    parameters.Add("oauth_consumer_key", consumerKey);
    parameters.Add("oauth_nonce", Guid.NewGuid().ToString("N"));
    parameters.Add("oauth_signature_method", "HMAC-SHA1");
    parameters.Add("oauth_timestamp", ((int)(DateTime.UtcNow - new DateTime(1970, 1, 1)).TotalSeconds).ToString());
    parameters.Add("oauth_token", accessToken);
    parameters.Add("oauth_version", "1.0");
    // Sort the parameters alphabetically
    var sortedParameters = parameters.OrderBy(p => p.Key).ToList();
    // Concatenate the parameters into a query string
    string parameterString = string.Join("&", sortedParameters.Select(p => string.Format("{0}={1}", p.Key, HttpUtility.UrlEncode(p.Value, Encoding.UTF8))));
    // Create the base string
    string baseString = string.Format("{0}&{1}&{2}", httpMethod, HttpUtility.UrlEncode(url, Encoding.UTF8), HttpUtility.UrlEncode(parameterString, Encoding.UTF8));
    // Create the signing key
    string signingKey = string.Format("{0}&{1}", HttpUtility.UrlEncode(consumerSecret, Encoding.UTF8), HttpUtility.UrlEncode(accessTokenSecret, Encoding.UTF8));
    // Create the HMAC-SHA1 hash
    using (HMACSHA1 hmac = new HMACSHA1(Encoding.UTF8.GetBytes(signingKey)))
    {
        byte[] hashBytes = hmac.ComputeHash(Encoding.UTF8.GetBytes(baseString));
        return Convert.ToBase64String(hashBytes);
    }
}
```
