        // Create a queue to store the ladder
        Queue<List<String>> ladderQueue = new LinkedList<>();
        // Create a visited set to avoid revisiting words
        Set<String> visited = new HashSet<>();

        // Add the start word to the ladder queue
        List<String> startLadder = new ArrayList<>();
        startLadder.add(start);
        ladderQueue.add(startLadder);

        // Loop until the ladder queue is empty
        while (!ladderQueue.isEmpty()) {
            // Get the next ladder from the queue
            List<String> ladder = ladderQueue.poll();
            // Get the last word in the ladder
            String lastWord = ladder.get(ladder.size() - 1);

            // Check if the last word is the target word
            if (lastWord.equals(end)) {
                return ladder;
            }

            // Check if the last word has already been visited
            if (visited.contains(lastWord)) {
                continue;
            }

            // Mark the last word as visited
            visited.add(lastWord);

            // Find all valid English words that can be formed by changing one letter of the last word
            List<String> nextWords = getNextWords(lastWord);

            // Add each next word to a new ladder and add the ladder to the queue
            for (String nextWord : nextWords) {
                List<String> nextLadder = new ArrayList<>(ladder);
                nextLadder.add(nextWord);
                ladderQueue.add(nextLadder);
            }
        }

        // No ladder was found
        return null;
    }
    private List<String> getNextWords(String word) {
        List<String> nextWords = new ArrayList<>();

        // Change each letter of the word and check if the resulting word is valid English and only contains letters from the periodic table of elements
        for (int i = 0; i < word.length(); i++) {
            for (char c = 'a'; c <= 'z'; c++) {
                if (c == word.charAt(i)) {
                    continue;
                }

                String nextWord = word.substring(0, i) + c + word.substring(i + 1);

                if (englishWords.contains(nextWord) && elementsWords.contains(nextWord)) {
