            }
            Console.WriteLine("Triadic colors:");
            foreach (Color triadicColor in triadicColors)
            {
                Console.WriteLine($"- {ColorTranslator.ToHtml(triadicColor)}");
            }
            // Apply color psychology principles
            string baseColorName = GetColorName(baseColor);
            string complementaryColorName = GetColorName(complementaryColor);
            string analogousColorsName = GetColorNames(analogousColors);
            string triadicColorsName = GetColorNames(triadicColors);
