```matlab
% Define the number of drones in the swarm
numDrones = 10;
% Define the size of the 3D space
spaceSize = 100;
% Define the maximum velocity of each drone
maxVelocity = 5;
% Define the initial positions and velocities of the drones
positions = spaceSize * (rand(numDrones, 3) - 0.5);
velocities = maxVelocity * (rand(numDrones, 3) - 0.5);
% Define the positions of any obstacles in the environment
obstaclePositions = [10, 10, 10; -20, -20, -20];
% Define the radius of each obstacle
obstacleRadii = [5, 10];
% Define the time step for the simulation
timeStep = 0.1;
% Define the duration of the simulation
duration = 10;
% Simulate the movement of the drones
for t = 0:timeStep:duration

    % Calculate the distances between each drone and each obstacle
    distances = pdist2(positions, obstaclePositions);

    % Check if any drones are colliding with any obstacles
    collisions = any(distances < obstacleRadii, 2);

    % Update the velocities of the drones
    for i = 1:numDrones

        % Calculate the average velocity of nearby drones
        nearbyDrones = find(distances(i, :) < 10);
        if ~isempty(nearbyDrones)
            avgVelocity = mean(velocities(nearbyDrones, :));
        else
            avgVelocity = [0, 0, 0];
        end

        % Calculate the avoidance velocity for any nearby obstacles
        obstacleDistances = distances(i, :) - obstacleRadii;
        obstacleDistances(obstacleDistances < 0) = 0;
        obstacleVelocity = sum(bsxfun(@times, obstaclePositions, obstacleDistances), 1);

        % Update the velocity of the drone
        velocities(i, :) = velocities(i, :) + 0.1 * (avgVelocity + obstacleVelocity - velocities(i, :));

        % Limit the velocity of the drone
        speed = norm(velocities(i, :));
        if speed > maxVelocity
            velocities(i, :) = maxVelocity * velocities(i, :) / speed;
        end

        % Update the position of the drone
        positions(i, :) = positions(i, :) + velocities(i, :) * timeStep;

        % Check if the drone is colliding with any obstacles
        if collisions(i)
            % Reset the position of the drone
            positions(i, :) = spaceSize * (rand(1, 3) - 0.5);
            velocities(i, :) = maxVelocity * (rand(1, 3) - 0.5);
        end

    end

    % Plot the positions of the drones and obstacles
    clf;
    hold on;
    scatter3(positions(:, 1), positions(:, 2), positions(:, 3), 'filled', 'b');
    scatter3(obstaclePositions(:, 1), obstaclePositions(:, 2), obstaclePositions(:, 3), 'filled', 'r');
    xlim([-spaceSize, spaceSize]);
    ylim([-spaceSize, spaceSize]);
    zlim([-spaceSize, spaceSize]);
    xlabel('X');
    ylabel('Y');
    zlabel('Z');
    view(3);
    drawnow;

end
