```java
import java.util.Random;
public class GeneticAlgorithm {
    private static final double MUTATION_RATE = 0.05;
    private static final int POPULATION_SIZE = 100;
    private static final int GENERATIONS = 1000;
    private static final int ELITE_COUNT = 10;
    private static final int CHROMOSOME_LENGTH = 6;
    private static final int MAX_RAINFALL = 500;
    private static final int MIN_RAINFALL = 0;
    private static final int TARGET_FITNESS = 100;
    private static int[][] dataset = {
            {50, 75, 100, 150, 200, 300, 400, 350, 250, 150},
            {100, 150, 200, 250, 300, 350, 400, 350, 250, 200},
            {150, 200, 250, 300, 350, 400, 400, 350, 300, 250},
            {200, 250, 300, 350, 400, 450, 450, 400, 350, 300},
            {250, 300, 350, 400, 450, 500, 500, 450, 400, 350},
            {300, 350, 400, 450, 500, 450, 400, 350, 300, 250},
            {350, 400, 450, 500, 450, 400, 350, 300, 250, 200},
            {400, 450, 500, 450, 400, 350, 300, 250, 200, 150},
            {450, 500, 450, 400, 350, 300, 250, 200, 150, 100},
            {500, 450, 400, 350, 300, 250, 200, 150, 100, 50}
    };
    public static void main(String[] args) {
        Population population = new Population(POPULATION_SIZE, CHROMOSOME_LENGTH);
        population.evaluateFitness(dataset);
        int generation = 0;
        while (generation < GENERATIONS && population.getFittest().getFitness() < TARGET_FITNESS) {
            System.out.println("Generation " + generation + " Fittest: " + population.getFittest().getFitness());
            population = evolvePopulation(population);
            generation++;
        }
        System.out.println("Solution found in generation " + generation);
        System.out.println("Fitness: " + population.getFittest().getFitness());
        System.out.println("Chromosome: " + population.getFittest().toString());
    }
    private static Population evolvePopulation(Population population) {
        Population newPopulation = new Population(population.size(), CHROMOSOME_LENGTH);
        for (int i = 0; i < ELITE_COUNT; i++) {
            newPopulation.setChromosome(i, population.getFittest());
        }
        for (int i = ELITE_COUNT; i < population.size(); i++) {
            Chromosome parent1 = selectParent(population);
            Chromosome parent2 = selectParent(population);
            Chromosome child = crossover(parent1, parent2);
            mutate(child);
            newPopulation.setChromosome(i, child);
        }
        newPopulation.evaluateFitness(dataset);
        return newPopulation;
    }
    private static Chromosome selectParent(Population population) {
        Random random = new Random();
        Population tournament = new Population(5, CHROMOSOME_LENGTH);
        for (int i = 0; i < 5; i++) {
            tournament.setChromosome(i, population.getChromosome(random.nextInt(population.size())));
        }
        return tournament.getFittest();
    }
    private static Chromosome crossover(Chromosome parent1, Chromosome parent2) {
        Random random = new Random();
        Chromosome child = new Chromosome(CHROMOSOME_LENGTH);
        int crossoverPoint = random.nextInt(CHROMOSOME_LENGTH);
        for (int i = 0; i < CHROMOSOME_LENGTH; i++) {
            if (i < crossoverPoint) {
                child.setGene(i, parent1.getGene(i));
            } else {
                child.setGene(i, parent2.getGene(i));
            }
        }
        return child;
    }
    private static void mutate(Chromosome chromosome) {
        Random random = new Random();
        for (int i = 0; i < CHROMOSOME_LENGTH; i++) {
            if (random.nextDouble() < MUTATION_RATE) {
                chromosome.setGene(i, random.nextInt(MAX_RAINFALL - MIN_RAINFALL) + MIN_RAINFALL);
            }
        }
    }
}
class Chromosome {
    private int[] genes;
    private int fitness;
    public Chromosome(int length) {
        genes = new int[length];
        fitness = 0;
    }
    public int getGene(int index) {
        return genes[index];
    }
    public void setGene(int index, int value) {
        genes[index] = value;
        fitness = 0;
    }
    public int getFitness() {
        return fitness;
    }
    public void evaluateFitness(int[][] dataset) {
        int sum = 0;
        for (int i = 0; i < dataset[0].length; i++) {
            int rainfall = dataset[dataset.length - 1][i];
            for (int j = 0; j < genes.length; j++) {
                rainfall += genes[j];
            }
            if (rainfall < 100) {
                sum++;
            }
        }
        fitness = sum * 10;
    }
    @Override
    public String toString() {
        StringBuilder builder = new StringBuilder();
        for (int gene : genes) {
            builder.append(gene).append(" ");
        }
        return builder.toString();
    }
}
class Population {
    private Chromosome[] chromosomes;
    public Population(int size, int length) {
        chromosomes = new Chromosome[size];
        for (int i = 0; i < size; i++) {
            chromosomes[i] = new Chromosome(length);
        }
    }
    public Chromosome getChromosome(int index) {
        return chromosomes[index];
    }
    public void setChromosome(int index, Chromosome chromosome) {
        chromosomes[index] = chromosome;
    }
    public Chromosome getFittest() {
        Chromosome fittest = chromosomes[0];
        for (int i = 1; i < size(); i++) {
            if (chromosomes[i].getFitness() > fittest.getFitness()) {
                fittest = chromosomes[i];
            }
        }
        return fittest;
    }
    public void evaluateFitness(int[][] dataset) {
        for (Chromosome chromosome : chromosomes) {
            chromosome.evaluateFitness(dataset);
        }
    }
    public int size() {
        return chromosomes.length;
    }
}
```
This code uses a genetic algorithm to optimize a chromosome of length 6, where each gene represents the amount of rainfall for a month. The fitness function evaluates the chromosome based on the number of months with rainfall less than 100mm in the past 10 years. The target fitness is set to 100, which means the model is considered accurate if it can predict all 10 years with no more than one month of drought per year.
