    class Imputer
    {
        public List<Tuple<int, int, int, int, int>> Impute(List<Tuple<int, int, int, int, int>> data)
        {
            var imputedData = new List<Tuple<int, int, int, int, int>>();
            for (int i = 0; i < data.Count; i++)
            {
                var row = data[i];
                if (row.Item1 == 0 || row.Item2 == 0 || row.Item3 == 0 || row.Item4 == 0 || row.Item5 == 0)
                {
                    row = Tuple.Create(row.Item1 == 0 ? 24 : row.Item1, row.Item2 == 0 ? 1 : row.Item2, row.Item3 == 0 ? 50000 : row.Item3, row.Item4 == 0 ? 0 : row.Item4, row.Item5 == 0 ? 0 : row.Item5);
                }
                imputedData.Add(row);
            }
            return imputedData;
        }
    }
    class MinMaxScaler
    {
        private double[] minValues;
        private double[] maxValues;
        public List<Tuple<int, int, int, int, int>> Scale(List<Tuple<int, int, int, int, int>> data)
        {
            minValues = new double[data[0].Item1];
            maxValues = new double[data[0].Item1];
            for (int i = 0; i < data[0].Item1; i++)
            {
                minValues[i] = double.MaxValue;
                maxValues[i] = double.MinValue;
            }
            // Find the minimum and maximum values for each feature
            foreach (var row in data)
            {
                for (int i = 0; i < row.Item1; i++)
                {
                    if (row.Item1 < minValues[i])
                    {
                        minValues[i] = row.Item1;
                    }
                    if (row.Item1 > maxValues[i])
                    {
                        maxValues[i] = row.Item1;
                    }
                }
            }
            // Scale the data
            var scaledData = new List<Tuple<int, int, int, int, int>>();
            foreach (var row in data)
            {
                var scaledRow = new List<double>();
                for (int i = 0; i < row.Item1; i++)
                {
                    scaledRow.Add((row.Item1 - minValues[i]) / (maxValues[i] - minValues[i]));
                }
                scaledData.Add(Tuple.Create(row.Item1, row.Item2, row.Item3, row.Item4, row.Item5));
            }
            return scaledData;
        }
    }
    class TrainTestSplitter
    {
        public (List<double[]>, double[], List<double[]>, double[]) Split(List<Tuple<int, int, int, int, int>> data, double testSize)
        {
            var X = data.Select(row => new double[] { row.Item1, row.Item2, row.Item3, row.Item4 }).ToList();
            var y = data.Select(row => row.Item5).ToArray();
            var splitIndex = (int)(data.Count * (1 - testSize));
            var X_train = X.Take(splitIndex).ToList();
            var y_train = y.Take(splitIndex).ToArray();
            var X_test = X.Skip(splitIndex).ToList();
            var y_test = y.Skip(splitIndex).ToArray();
            return (X_train, y_train, X_test, y_test);
        }
    }
    class SelectKBest
    {
        public List<double[]> Select(List<double[]> X, double[] y, int k)
        {
            var scores = new List<double>();
            for (int i = 0; i < X[0].Length; i++)
            {
                var featureValues = X.Select(row => row[i]).ToArray();
                var score = ChiSquareTest(featureValues, y);
                scores.Add(score);
            }
            var sortedIndices = scores.Select((score, index) => new { score, index }).OrderByDescending(x => x.score).Select(x => x.index).ToArray();
            var selectedIndices = sortedIndices.Take(k).ToArray();
            var selectedFeatures = new List<double[]>();
            foreach (var row in X)
            {
                var selectedRow = new List<double>();
                foreach (var index in selectedIndices)
                {
                    selectedRow.Add(row[index]);
                }
                selectedFeatures.Add(selectedRow.ToArray());
            }
            return selectedFeatures;
        }
        public List<double[]> Transform(List<double[]> X)
