        }
        private double ChiSquareTest(double[] featureValues, double[] y)
        {
            var contingencyTable = new double[2, 2];
            for (int i = 0; i < featureValues.Length; i++)
            {
                if (featureValues[i] == 0 && y[i] == 0)
                {
                    contingencyTable[0, 0]++;
                }
                else if (featureValues[i] == 0 && y[i] == 1)
                {
                    contingencyTable[0, 1]++;
                }
                else if (featureValues[i] == 1 && y[i] == 0)
                {
                    contingencyTable[1, 0]++;
                }
                else if (featureValues[i] == 1 && y[i] == 1)
                {
                    contingencyTable[1, 1]++;
                }
            }
            var rowSums = new double[] { contingencyTable[0, 0] + contingencyTable[0, 1], contingencyTable[1, 0] + contingencyTable[1, 1] };
            var colSums = new double[] { contingencyTable[0, 0] + contingencyTable[1, 0], contingencyTable[0, 1] + contingencyTable[1, 1] };
            var total = contingencyTable[0, 0] + contingencyTable[0, 1] + contingencyTable[1, 0] + contingencyTable[1, 1];
            var expected = new double[2, 2];
            for (int i = 0; i < 2; i++)
            {
                for (int j = 0; j < 2; j++)
                {
                    expected[i, j] = rowSums[i] * colSums[j] / total;
                }
            }
            var chiSquare = 0.0;
            for (int i = 0; i < 2; i++)
            {
                for (int j = 0; j < 2; j++)
                {
                    chiSquare += Math.Pow(contingencyTable[i, j] - expected[i, j], 2) / expected[i, j];
                }
            }
            return chiSquare;
        }
    }
