This is the solution to the 149th Euler project problem: 

from itertools import permutations
from math import sqrt

def is_prime(n):
    if n < 2:
        return False
    for i in range(2, int(sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def prime_additives(n):
    if n < 2:
        return 0
    if n == 2:
        return 1
    primes = [i for i in range(2, n) if is_prime(i)]
    summations = set()
    for perm in permutations(primes):
        current = 0
        summations.add(current)    
        for p in perm:
            current += p
            if current < n:
                summations.add(current)
            else:
                break
            if len(summations) > 49:
                return len(summations)

    return len(summations)

print(prime_additives(100))

