#include <iostream>
#include <cstdlib>
#include <ctime>
#include <vector>
#include <string>
using namespace std;
// Quiz question structure
struct Question {
    string question;
    vector<string> options;
    char correctAnswer;
};
// Quiz grading function
int gradeQuiz(const vector<Question>& quiz) {
    int score = 0;
    for (const Question& q : quiz) {
        cout << q.question << endl;
        for (int i = 0; i < q.options.size(); i++) {
            cout << static_cast<char>('A' + i) << ") " << q.options[i] << endl;
        }
        char answer;
        cout << "Enter your answer (A, B, C, or D): ";
        cin >> answer;
        if (toupper(answer) == q.correctAnswer) {
            score++;
        }
    }
    return score;
}
int main() {
    // Seed the random number generator
    srand(time(nullptr));
    // Quiz questions and answers
    vector<Question> quiz = {
        {"What is the time complexity of searching for an element in a hash table?", {"A. O(1)", "B. O(n)", "C. O(log n)", "D. O(n log n)"}, 'A'},
        {"What is the time complexity of inserting an element into a heap?", {"A. O(1)", "B. O(log n)", "C. O(n)", "D. O(n log n)"}, 'B'},
