        {"What is the time complexity of merging two sorted arrays into one sorted array?", {"A. O(n)", "B. O(n log n)", "C. O(n^2)", "D. O(log n)"}, 'B'},
        {"What is the time complexity of sorting an array using bubble sort?", {"A. O(n)", "B. O(n log n)", "C. O(n^2)", "D. O(log n)"}, 'C'}
    };
    // Shuffle the quiz questions
    random_shuffle(quiz.begin(), quiz.end());
    // Generate the quiz HTML
    cout << "<html>" << endl;
    cout << "<head>" << endl;
    cout << "<title>Quiz</title>" << endl;
    cout << "</head>" << endl;
    cout << "<body>" << endl;
    cout << "<h1>Quiz</h1>" << endl;
    for (const Question& q : quiz) {
        cout << "<h2>" << q.question << "</h2>" << endl;
        for (int i = 0; i < q.options.size(); i++) {
            cout << "<p>" << static_cast<char>('A' + i) << ") " << q.options[i] << "</p>" << endl;
        }
    }
    cout << "<button onclick=\"gradeQuiz()\">Grade Quiz</button>" << endl;
    cout << "<script>" << endl;
    cout << "function gradeQuiz() {" << endl;
    cout << "var quiz = " << endl;
    cout << "[" << endl;
    for (int i = 0; i < quiz.size(); i++) {
        cout << "{" << endl;
        cout << "\"question\": \"" << quiz[i].question << "\"," << endl;
        cout << "\"options\": [" << endl;
        for (int j = 0; j < quiz[i].options.size(); j++) {
            cout << "\"" << quiz[i].options[j] << "\"";
            if (j < quiz[i].options.size() - 1) {
                cout << ",";
            }
            cout << endl;
        }
        cout << "]," << endl;
        cout << "\"correctAnswer\": \"" << quiz[i].correctAnswer << "\"" << endl;
        cout << "}";
        if (i < quiz.size() - 1) {
            cout << ",";
        }
        cout << endl;
    }
    cout << "];" << endl;
    cout << "var score = 0;" << endl;
    cout << "for (var i = 0; i < quiz.length; i++) {" << endl;
    cout << "var answer = prompt(quiz[i].question + \"\\n\" + quiz[i].options.join(\"\\n\"));" << endl;
    cout << "if (answer.toUpperCase() === quiz[i].correctAnswer) {" << endl;
    cout << "score++;" << endl;
    cout << "}" << endl;
    cout << "}" << endl;
    cout << "alert(\"Your score is \" + score + \"/\" + quiz.length);" << endl;
    cout << "}" << endl;
    cout << "</script>" << endl;
    cout << "</body>" << endl;
    cout << "</html>" << endl;
    // Grade the quiz and output the result
    int score = gradeQuiz(quiz);
    cout << "Your score is " << score << "/" << quiz.size() << endl;
    return 0;
}

