```
package main
import (
    "fmt"
    "sort"
)
func main() {
    income := []float64{5000, 8000, 12000, 15000, 25000, 35000, 45000, 55000, 70000, 90000}
    num_people := []float64{10, 20, 30, 50, 70, 90, 120, 150, 200, 250}
    // Calculate the total income and total number of people
    total_income := 0.0
    total_people := 0.0
    for i := 0; i < len(income); i++ {
        total_income += income[i] * num_people[i]
        total_people += num_people[i]
    }
    // Calculate the average income
    avg_income := total_income / total_people
    // Create a list of tuples (income, num_people) and sort it by income in ascending order
    data := make([][2]float64, len(income))
    for i := range data {
        data[i] = [2]float64{income[i], num_people[i]}
    }
    sort.Slice(data, func(i, j int) bool {
        return data[i][0] < data[j][0]
    })
    // Calculate the cumulative percentage of people and cumulative percentage of income
    cum_people := make([]float64, len(data))
    cum_income := make([]float64, len(data))
    for i := range data {
        cum_people[i] = data[i][1] / total_people
        cum_income[i] = (data[i][0] * data[i][1]) / total_income
        if i > 0 {
            cum_people[i] += cum_people[i-1]
            cum_income[i] += cum_income[i-1]
        }
    }
    // Calculate the Gini coefficient
    var gini float64
    for i := range data {
        gini += (cum_people[i] + cum_income[i]) * (1 - cum_income[i])
    }
    gini = 1 - 2*gini/float64(len(data)-1)
    // Print the results
    fmt.Printf("Total income: %.2f\n", total_income)
    fmt.Printf("Total people: %.2f\n", total_people)
    fmt.Printf("Average income: %.2f\n", avg_income)
    fmt.Printf("Gini coefficient: %.2f\n", gini)
    // Plot the Lorenz curve
    plotLorenzCurve(cum_people, cum_income)
}
// plotLorenzCurve plots the Lorenz curve using the cumulative percentage of people and income
