        string name;
        uint256 votes;
    }
    mapping (uint256 => Option) public options;
    uint256 public optionCount;
    function addOption(string _name) public {
        options[optionCount] = Option(_name, 0);
        optionCount++;
    }
    function vote(uint256 _optionId) public {
        options[_optionId].votes++;
    }
    function getWinner() public view returns (string) {
        uint256 winnerId = 0;
        uint256 winnerVotes = 0;
        for (uint256 i = 0; i < optionCount; i++) {
