```C#
using System;
using System.Linq;

class WhodunnitGame
{
    static void Main()
    {
        string[] suspects = new string[] {"John", "Emily", "David", "Lucy", "Michael"};
        string[] weapons = new string[] {"knife", "gun", "poison", "hammer", "rope"};
        string[] locations = new string[] {"bedroom", "kitchen", "garden", "study", "bathroom"};

        string killer = suspects[new Random().Next(5)];
        string murderWeapon = weapons[new Random().Next(5)];
        string murderLocation = locations[new Random().Next(5)];

        Console.WriteLine($"The victim was found dead in the {murderLocation}, having been killed with a {murderWeapon}. There are 5 suspects in the case: {string.Join(", ", suspects)}. You have been brought in to interrogate the suspects and solve the case. Good luck!");

        // Shuffle the suspects array
        Random rng = new Random();
        string[] shuffledSuspects = suspects.OrderBy(x => rng.Next()).ToArray();

        bool[] interrogated = new bool[5]; // Keep track of which suspects have been interrogated
        for (int i = 0; i < 5; i++)
        {
            // Prompt player to choose the next suspect to interrogate
            Console.WriteLine($"Which suspect would you like to interrogate next? ({string.Join(", ", shuffledSuspects.Where((_, index) => !interrogated[index]))})");
            string nextSuspect = Console.ReadLine();

            // Check if the suspect is valid and hasn't been interrogated already
            int index = Array.IndexOf(suspects, nextSuspect);
            if (index == -1)
            {
                Console.WriteLine("That suspect is not in the case. Please choose one of the 5 suspects.");
                i--; // Don't count this as an iteration
            }
            else if (interrogated[index])
            {
                Console.WriteLine("That suspect has already been interrogated. Please choose another suspect.");
                i--; // Don't count this as an iteration
            }
            else
            {
                // Interrogate the suspect and mark them as interrogated
                Console.WriteLine($"{nextSuspect}'s alibi: '{GetAlibi(nextSuspect, killer, murderWeapon, murderLocation)}'");
                interrogated[index] = true;
            }
        }

        // Prompt player to make a guess
        Console.WriteLine("You have interrogated all suspects. Now it's time to make your guess! Enter a suspect, a weapon, and a location separated by spaces:");
        string[] guess = Console.ReadLine().Split();
        bool solved = CheckGuess(guess[0], guess[1], guess[2], killer, murderWeapon, murderLocation);

        if (solved)
        {
            Console.WriteLine("Congratulations, you solved the case!");
        }
        else
        {
            Console.WriteLine("Sorry, your guess was incorrect. The killer was {0}, the murder weapon was {1}, and the murder location was {2}.", killer, murderWeapon, murderLocation);
        }
    }

    static string GetAlibi(string suspect, string killer, string weapon, string location)
    {
        switch (suspect)
        {
            case "John":
                return "I was at the cinema with my girlfriend at the time of the murder.";
            case "Emily":
                return "I was working late at the office and didn't leave until after midnight.";
            case "David":
