                string input = Console.ReadLine();
                bool validInput = Int32.TryParse(input, out choice);

                if (validInput && board[choice] != 'X' && board[choice] != 'O')
                {
                    if (player % 2 == 0)
                    {
                        board[choice] = 'O';
                        player++;
                    }
                    else
                    {
                        board[choice] = 'X';
                        player++;
                    }
                }
                else
                {
                    Console.WriteLine("Invalid move! Please enter a valid number.");
                    Console.ReadKey();
                }

                flag = CheckWin();

            } while (flag != 1 && flag != -1);

            Console.Clear();
            Board();

