// Functions to handle routes
const notesFile = path.join(__dirname, 'notes.json');

async function createNote(req, res) {
    const { title, content } = req.body;

    if (!title || !content) {
        return res.status(400).json({ error: 'Title and content are required' });
    }

    let notes = await fs.readJson(notesFile).catch(() => []);

    const newNote = {
        id: Date.now(),
        title,
        content,
        timestamp: new Date().toISOString()
    };

    notes.push(newNote);
    await fs.writeJson(notesFile, notes);

    res.status(201).json(newNote);
}

async function getNotes(req, res) {
    const notes = await fs.readJson(notesFile).catch(() => []);
    res.json(notes);
}

async function getNoteById(req, res) {
    const notes = await fs.readJson(notesFile).catch(() => []);
    const note = notes.find(note => note.id === parseInt(req.params.id));

    if (!note) {
        return res.status(404).json({ error: 'Note not found' });
    }

    res.json(note);
}

async function updateNote(req, res) {
    const { title, content } = req.body;
    const notes = await fs.readJson(notesFile).catch(() => []);
    const index = notes.findIndex(note => note.id === parseInt(req.params.id));

    if (index === -1) {
        return res.status(404).json({ error: 'Note not found' });
    }

    if (title) {
        notes[index].title = title;
    }

    if (content) {
        notes[index].content = content;
    }

    await fs.writeJson(notesFile, notes);
    res.json(notes[index]);
}

async function deleteNote(req, res) {
    const notes = await fs.readJson(notesFile).catch(() => []);
    const index = notes.findIndex(note => note.id === parseInt(req.params.id));

    if (index === -1) {
        return res.status(404).json({ error: 'Note not found' });
    }

    const deletedNote = notes.splice(index, 1);
    await fs.writeJson(notesFile, notes);

    res.json(deletedNote[0]);
}

